xpath.script:
$.fn.optVisible = function( show ) {
                if( show ) {
                    this.filter( "span > option" ).unwrap();
                } else {
                    this.filter( ":not(span > option)" ).wrap( "" ).parent().hide();
                }
                return this;
            }
$(document).on('change', 'select[name='brand_id']', function() {
    var $div = $("div[id='model_div']");
    var $div2 = $("div[id='year_div']");
    var $select = $("select[name='model_id']");
    $select.find("option:not(:first)").optVisible(false);
    $select.find("option[data-brand_id="+($(this).val() || 0)+"]").optVisible(true);
    $select.find("option[data-brand_id="+($(this).val() || 0)+"]").show().size();
    $div.hide();
    $div2.hide();
    if ($(this).val()!="0"){
        $div.show();
    };
});
$(document).on('change', 'select[name='model_id']', function() {
    var $div = $("div[id='year_div']");
    var $select = $("select[name='year_ids']");
    $select.find("option:not(:first)").optVisible(false);
    $select.find("option[data-model_id="+($(this).val() || 0)+"]").optVisible(true);
    $select.find("option[data-model_id="+($(this).val() || 0)+"]").show().size();
    $div.hide();
    if ($(this).val()!="0"){
        $div.show();
    };
};
